import api from './apiConfig';

export const getAllMotorcycles = async (id) => {
  const resp = await api.get(`/users/${id}/motorcycles`)
  return resp.data
}

export const getOneMotorcycle = async (id) => {
  const resp = await api.get(`/users/${id}/motorcycles/${id}`)
  return resp.data
}

export const postMotorcycle = async (motorcycleData) => {
  const resp = await api.post(`/users/${id}/motorcycles`, { motorcycle: motorcycleData })
  return resp.data
}
